// Filter survey responses that have comments but no sentiment analysis yet
const apiResponse = $input.first().json;

// Validar estructura de respuesta
if (!apiResponse.success || !apiResponse.data) {
  console.log('‚ùå API response invalid or empty');
  return [];
}

const recentSurveys = apiResponse.data.recentSurveys || [];
console.log(`üìä Total surveys received: ${recentSurveys.length}`);

// Filtrar encuestas con comentarios sin procesar
const unprocessedComments = recentSurveys.filter(survey => {
  // Must have a comment
  if (!survey.comment || survey.comment.trim() === '') return false;

  // Must NOT have sentiment already analyzed
  if (survey.sentiment) return false;

  // Must have a valid rating (1-5)
  if (!survey.rating || survey.rating < 1 || survey.rating > 5) return false;

  return true;
});

console.log(`üí¨ Surveys with comments: ${recentSurveys.filter(s => s.comment).length}`);
console.log(`üîÑ Comments to process: ${unprocessedComments.length}`);

// Limit to 20 per execution to avoid overwhelming Gemini API
const toProcess = unprocessedComments.slice(0, 20);

if (toProcess.length === 0) {
  console.log('‚úÖ No new comments to process');
  return [];
}

return toProcess.map(survey => ({ json: survey }));

// Extract sentiment analysis from Gemini API response
const items = $input.all();
const results = [];

for (const item of items) {
  try {
    const response = item.json;

    // Get original survey data from Loop Over Comments
    const originalData = $node["üîÑ Loop Over Comments"].json;

    let analysis = null;
    let parseError = null;

    // Try to extract JSON from response
    if (response?.candidates && response.candidates.length > 0) {
      const candidate = response.candidates[0];

      if (candidate?.content?.parts && candidate.content.parts.length > 0) {
        const text = candidate.content.parts[0].text || '';

        try {
          // Parse JSON response
          analysis = JSON.parse(text);
          console.log(`‚úÖ Successfully parsed sentiment for ${originalData.from}`);
        } catch (jsonError) {
          console.error(`‚ùå Failed to parse JSON: ${jsonError.message}`);
          console.error(`Raw text: ${text}`);
          parseError = jsonError.message;
        }
      }
    }

    // Validate analysis structure
    if (analysis && typeof analysis === 'object') {
      // Ensure required fields exist
      const validatedAnalysis = {
        sentiment: analysis.sentiment || 'unknown',
        sentiment_score: parseFloat(analysis.sentiment_score) || 0.5,
        topics: Array.isArray(analysis.topics) ? analysis.topics.slice(0, 3) : [],
        key_phrases: Array.isArray(analysis.key_phrases) ? analysis.key_phrases.slice(0, 3) : [],
        urgency: analysis.urgency || 'low',
        actionable: Boolean(analysis.actionable),
        action_type: analysis.action_type || 'none',
        summary_es: analysis.summary_es || 'Sin resumen',
        customer_intent: analysis.customer_intent || 'unknown',
        nps_alignment: analysis.nps_alignment || 'passive'
      };

      results.push({
        json: {
          ...originalData,
          sentiment_analysis: validatedAnalysis,
          processed_at: new Date().toISOString(),
          processing_status: 'success'
        }
      });
    } else {
      // Failed to get valid analysis
      console.error(`‚ùå Invalid analysis structure for ${originalData.from}`);

      results.push({
        json: {
          ...originalData,
          sentiment_analysis: {
            sentiment: 'error',
            sentiment_score: 0,
            topics: [],
            key_phrases: [],
            urgency: 'low',
            actionable: false,
            action_type: 'none',
            summary_es: 'Error al procesar',
            customer_intent: 'unknown',
            nps_alignment: 'passive'
          },
          processed_at: new Date().toISOString(),
          processing_status: 'error',
          error_message: parseError || 'Invalid response structure'
        }
      });
    }

  } catch (error) {
    console.error('‚ùå Error processing item:', error);

    const originalData = $node["üîÑ Loop Over Comments"].json;

    results.push({
      json: {
        ...originalData,
        sentiment_analysis: null,
        processed_at: new Date().toISOString(),
        processing_status: 'fatal_error',
        error_message: error.message
      }
    });
  }
}

return results;

