// Calculate sentiment statistics for this batch
const items = $input.all();

// Initialize counters
const stats = {
  total: items.length,
  positive: 0,
  neutral: 0,
  negative: 0,
  very_negative: 0,
  errors: 0,
  actionable: 0,
  high_urgency: 0,
  critical_urgency: 0
};

// Count sentiments and urgencies
for (const item of items) {
  const analysis = item.json.sentiment_analysis || {};
  const sentiment = analysis.sentiment;

  switch (sentiment) {
    case 'positive':
      stats.positive++;
      break;
    case 'neutral':
      stats.neutral++;
      break;
    case 'negative':
      stats.negative++;
      break;
    case 'very_negative':
      stats.very_negative++;
      break;
    case 'error':
    case 'unknown':
      stats.errors++;
      break;
  }

  // Count actionable items
  if (analysis.actionable) {
    stats.actionable++;
  }

  // Count urgency levels
  if (analysis.urgency === 'high') {
    stats.high_urgency++;
  } else if (analysis.urgency === 'critical') {
    stats.critical_urgency++;
  }
}

// Calculate percentages
stats.positive_pct = ((stats.positive / stats.total) * 100).toFixed(1);
stats.negative_pct = (((stats.negative + stats.very_negative) / stats.total) * 100).toFixed(1);
stats.actionable_pct = ((stats.actionable / stats.total) * 100).toFixed(1);

// Add timestamp
stats.fecha_ejecucion = new Date().toISOString();
stats.fecha_formato = new Date().toLocaleString('es-MX', {
  timeZone: 'America/Mexico_City',
  dateStyle: 'medium',
  timeStyle: 'short'
});

console.log('üìä Sentiment Analysis Statistics:', stats);

return [{ json: stats }];
